                $adb->pquery("UPDATE vtiger_activationcode SET expiredate=?,buyid=?,usercode=? WHERE status IN(0,1) AND activationcodeid=?",array($expiredate,$dataA['activationcodeid'],$_REQUEST['usercode'],$recordModel->getId()));
