    /**
     * 在save后保存数据
     * @param $request
     */
    public function  afterSaveRecord($request){
        global $adb;
        if($request->get('record')){
            //编辑
            $sql="update vtiger_preinvoicedeferral set applicantdate=?,applicantid=?,applicantdays=?,applicantreason=? where deferralid=?";
            $adb->pquery($sql,array($request->get('applicantdate'),$request->get('applicantid'),$request->get('applicantdays'),$request->get('applicantreason'),$request->get('record')));
        }else{
            $sql="update vtiger_preinvoicedeferral set applicantdate=?,applicantid=?,applicantdays=?,applicantreason=?,invoiceid=?,userid=? where deferralid=?";
            $adb->pquery($sql,array($request->get('applicantdate'),$request->get('applicantid'),$request->get('applicantdays'),$request->get('applicantreason'),$request->get('invoiceid'),$request->get('userid'),$request->get('deferralid')));
            //触发工作流
            $preInvoiceDeferralModel=new PreInvoiceDeferral();
            $workflowsId=PreInvoiceDeferral_Record_Model::getWorkFlowId();
            $preInvoiceDeferralModel->makeWorkflows($request->get('module'),$workflowsId,$request->get('deferralid'),'');
        }
    }
